import React from 'react'
import {useEffect} from 'react'
import {useState} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
const AdvQ1 = () => {
const [crimes,setCrimes] = useState([])
const [coords,setCoords] = useState({
    lat:0,
    lng:0
  });

useEffect(()=>{
    const fetchAllCrimes = async()=>{
        try{
            const res = await axios.get("http://localhost:3306/Crimes/AdvQ1")
            setCrimes(res.data);
            console.log(res)
            console.log(res.data[0])
        }catch(err){
            console.log(err)
        }
    }
    fetchAllCrimes();
}, []);

const handleChange = (e) => {
    setCoords((prev)=>({...prev,[e.target.name]: e.target.value}));
}

const handleClick = async e => {
    e.preventDefault()
    try {
      const res = await axios.post("http://localhost:3306/callStoredProcedure", coords)
      setCrimes(res.data)
      console.log(res)
    }catch(err){
      console.log(err)
    }
  };

console.log(coords)

return (
    <div>
        <h1>Stored Procedure Results</h1>
        <div>
            <button className="backToCrimes"><Link to="/Crimes">Back to crimes</Link></button>
        </div>
        <div>
            <input type="number" placeholder='latitude' onChange={handleChange} name="lat"/>
        </div>
        <div>
            <input type="number" placeholder='longitude' onChange={handleChange} name="lng"/>
        </div>
        <button className="confirm" onClick={handleClick}>Confirm</button>
        <table>
            <thead>
                <tr>
                    <th>Crime ID</th>
                    <th>Location ID</th>
                    <th>Crime Type</th>
                    <th>Time</th>
                    <th>User Reported</th>
                    <th>Victim ID</th>
                </tr>
            </thead>
            {crimes.map(crime => (
                <tbody>
                    <tr>
                        <td>{crime.crimeID}</td>
                        <td>{crime.locationID}</td>
                        <td>{crime.crime_type}</td>
                        <td>{crime.time}</td>
                        <td>{crime.user_report}</td>
                        <td>{crime.victimID}</td>
                    </tr>
                </tbody>
            ))}
        </table>
    </div>
  )
}

export default AdvQ1
