import React from 'react'
import {useEffect} from 'react'
import {useState} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import Cookies from 'js-cookie'
import { GoogleMap, useLoadScript, MarkerF, WindowInfo} from '@react-google-maps/api'

var lat = 0
var lng = 0

const Crimes = () => {
const [crimes,setCrimes] = useState([])
const [selectedCrime, setSelectedCrime] = useState(null);

useEffect(()=>{
    const fetchAllCrimes = async()=>{
        try{
            const coords = {
                lat : 40.1020,
                lng : -88.2272
            }
            const res = await axios.post("http://localhost:3306/Crimes", coords)
            setCrimes(res.data);
            console.log(res)
            console.log(res.data[0].longitude)
        }catch(err){
            console.log(err)
        }
    }
    fetchAllCrimes();
    //var loggedInAs = document.getElementById("loggedInAs")
    //loggedInAs.innerHTML = "Logged in as <span class=\"curUser\">"+Cookies.get('username')+"</span>"
}, []);

const {isLoaded} = useLoadScript({googleMapsApiKey: "AIzaSyC0lAXu1gaIJAw-Q5Gibi3JTaRzFs-wc7I"});
if(!isLoaded) {return <div>Loading....</div>}

return (
    <div>
        <h1>Reported Crimes</h1>
        <span id="loggedInAs">Logged in as ...</span>
        <div>
            <button className="report"><Link to="/report">Report a Crime</Link></button>
            <button className="advQ1"><Link to="/advQ1">Stored Procedure</Link></button>
            <button className="toUsers"><Link to="/Users">View Users</Link></button>
        </div>
        <h5 id="long">Longitude: ?</h5> 
        <h5 id="lat">Latitude: ?</h5> 
        <div className="map_and_crimes">
            <div className="crime_table">
                <table className="crime_table_header">
                <thead>
                    <tr>
                        <th>Crime ID</th>
                        <th>Location ID</th>
                        <th>Crime Type</th>
                        <th>Time</th>
                        <th>User Reported</th>
                        <th>Victim ID</th>
                        <th>Longitude</th>
                        <th>Latitude</th>
                    </tr>
                </thead>
                </table>
                <div className="crime_table_body_wrapper">
                <table>
                    <tbody>
                    {crimes.map(crime => (
                        <tr key={crime.crimeID}>
                        <td>{crime.crimeID}</td>
                        <td>{crime.locationID}</td>
                        <td>{crime.crime_type}</td>
                        <td>{crime.time}</td>
                        <td>{crime.user_report}</td>
                        <td>{crime.victimID}</td>
                        <td>{crime.longitude}</td>
                        <td>{crime.latitude}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                </div>
            </div>
            <div className="map_container">
                <Map crimes={crimes}/>
            </div>
        </div>
        
    </div>
  )
}

export default Crimes

function Map({crimes}) {
//console.log(crimes[0].latitude + )
 //console.log(crimes[0].longitude + " " + crimes[0].latitude)
 return <GoogleMap zoom={15} center={{lat: 40.1020, lng:-88.2272}} mapContainerClassName='map' onClick={ev => {
    lat = ev.latLng.lat()
    lng = ev.latLng.lng()
    document.getElementById('lat').innerHTML = "Latitude: <span class=\"blue\">" + lat + "</span>";
    document.getElementById('long').innerHTML = "Longitude: <span class=\"blue\">" + lng + "</span>";
    }}>
    {crimes.map((crime, index) => (
        <MarkerF
          key={index}
          position={{ lat: crime.latitude, lng: crime.longitude }}
          onClick={() => {
            setSelectedCrime(crime);
          }}
          options={{
            icon: {
              url: 'https://maps.google.com/mapfiles/ms/icons/red-dot.png', // Replace with the URL of your icon
              scaledSize: new window.google.maps.Size(50, 50), // Set the size of the icon
            },
          }}
        />
      ))}
    {selectedCrime && (
        <InfoWindow
          position={{ lat: selectedCrime.latitude, lng: selectedCrime.longitude }}
          onCloseClick={() => {
            setSelectedCrime(null);
          }}
        >
          <div>
            <h4>{selectedCrime.crime_type}</h4>
            <p>
              Crime ID: {selectedCrime.crimeID} <br />
              Location ID: {selectedCrime.locationID} <br />
              Time: {selectedCrime.time} <br />
              User Reported: {selectedCrime.user_report} <br />
              Victim ID: {selectedCrime.victimID}
            </p>
          </div>
        </InfoWindow>
      )}
 </GoogleMap>
 }
