import React from 'react';
import {useState} from 'react';
import axios from 'axios'
import { useNavigate } from 'react-router-dom';
import { GoogleMap, useLoadScript, Markers} from '@react-google-maps/api';

const location = {
  address: '1600 Amphitheatre Parkway, Mountain View, california.',
  lat: 37.42216,
  lng: -122.08427,
}

export default function Report() {
  const [details, setDetails] = useState({
    crimeType: "",
    crimeLat: 0.0,
    crimeLong: 0.0,
    crimeDescription: "",
    victimSex: "",
    victimRace: "",
    victimAge: "",
    time: "",
    username: document.cookie.substring(0,20),
  });

  const handleChange = (e) => {
    setDetails((prev) => {
      const name = e.target.name;
      const value =  e.target.value;
      return {...prev, [name]: value}
    })
  };
  const handleSubmit =  async e=> {
    e.preventDefault()
    try {
      const res = await axios.post("http://localhost:3306/AddCrime", details)
      console.log(res)
    }catch(err){
      console.log(err)
    }
    
  }
  const {isLoaded} = useLoadScript({googleMapsApiKey: "AIzaSyC0lAXu1gaIJAw-Q5Gibi3JTaRzFs-wc7I"});
  if(!isLoaded) {return <div>Loading....</div>}
  return (
  <div>
    <h1>Report a crime!</h1>
    <form onSubmit={handleSubmit}>
      <h5>Crime Type: </h5> <input type="text" name="crimeType" onChange={handleChange} />
      <h5>Crime Lat: </h5> <input type="text" id="lat" name="crimeLat" onChange={handleChange}/>
      <h5>Crime Long: </h5> <input type="text" id="long" name="crimeLong" onChange={handleChange}/>
      <h5>Crime Description: </h5> <textarea  name="crimeDescription" onChange={handleChange}></textarea>
      <h5>Victim Sex: </h5> <input type="text" name="victimSex" onChange={handleChange}/>
      <h5>Time: </h5> <input type="number" name="time" onChange={handleChange}/>
      <h5>Victim Race: </h5> <input type="text" name="victimRace"onChange={handleChange}/>
      <h5>Victim Age: </h5> <input type="number"  name="victimAge"onChange={handleChange}/>
      <button type="submit">Submit Form</button>
    </form>
  <Map />
  </div>
  )
  }

function Map() {
 return <GoogleMap onClick={ev => {
    document.getElementById('lat').value = ev.latLng.lat();
    document.getElementById('long').value = ev.latLng.lng();
 }} zoom={15} center={{lat: 40.1020, lng:-88.2272}} mapContainerClassName='map'></GoogleMap>
}
