import React from 'react';
import {useState} from 'react';
import axios from 'axios'
import { useNavigate } from 'react-router-dom';
import { GoogleMap, useLoadScript, Markers} from '@react-google-maps/api';
import Cookies from 'js-cookie'

var lat = 0
var lng = 0
var loc_change = false

const Report = () => {
  const [details, setDetails] = useState({
    crimeType: "",
    crimeLat: 0,
    crimeLng: 0,
    crimeDescription: "",
    victimSex: "",
    victimRace: "",
    victimAge: "",
    time: "",
    username: Cookies.get('username'),
  });

  console.log(loc_change)
  if (loc_change) {
    setDetails((prev) => {
      const name = "crimeLat";
      const value = lat;
      return {...prev, [name]: value}
    })    
    setDetails((prev) => {
      const name = "crimeLng";
      const value = lng;
      return {...prev, [name]: value}
    })
    loc_change = false
    console.log(lat)
    console.log(lng)
    console.log(details)
  }

  const handleChange = (e) => {
    setDetails((prev) => {
      const name = e.target.name;
      const value =  e.target.value;
      return {...prev, [name]: value}
    })
  };

  const handleSubmit =  async e=> {
    e.preventDefault()
    try {
      console.log(details)
      const res = await axios.post("http://localhost:3306/AddCrime", details)
      console.log(res)
    }catch(err){
      console.log(err)
    }
    
  }
  const {isLoaded} = useLoadScript({googleMapsApiKey: "AIzaSyC0lAXu1gaIJAw-Q5Gibi3JTaRzFs-wc7I"});
  if(!isLoaded) {return <div>Loading....</div>}

  return (
  <div>
    <h1>Report a crime!</h1>
    <form onSubmit={handleSubmit}>
      <h5>Crime Type: </h5> <input type="text" name="crimeType" onChange={handleChange} />
      <h5 id="lat">Latitude: ?</h5> 
      <h5 id="long">Longitude: ?</h5> 
      <h5>Crime Description: </h5> <textarea  name="crimeDescription" onChange={handleChange}></textarea>
      <h5>Victim Sex: </h5> <input type="text" name="victimSex" onChange={handleChange}/>
      <h5>Time: </h5> <input type="number" name="time" onChange={handleChange}/>
      <h5>Victim Race: </h5> <input type="text" name="victimRace"onChange={handleChange}/>
      <h5>Victim Age: </h5> <input type="number"  name="victimAge"onChange={handleChange}/>
      <button type="submit">Submit Form</button>
    </form>
  <Map />
  </div>
  )
}

export default Report

function Map() {
 return <GoogleMap onClick={ev => {
    lat = ev.latLng.lat()
    lng = ev.latLng.lng()
    document.getElementById('lat').innerHTML = "Latitude: <span class=\"blue\">" + lat + "</span>";
    document.getElementById('long').innerHTML = "Longitude: <span class=\"blue\">" + lng + "</span>";
    loc_change = true
 }} zoom={15} center={{lat: 40.1020, lng:-88.2272}} mapContainerClassName='map'></GoogleMap>
}
