import React from 'react'
import {useEffect} from 'react'
import {useState} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import Cookies from 'js-cookie'
import Select from 'react-select'
import { GoogleMap, useLoadScript, MarkerF, InfoWindow} from '@react-google-maps/api'
import crimeTypeOptions from '../components/crimeTypeOptions.js'; 
import raceOptions from '../components/raceOptions.js'; 

var lat = 0
var lng = 0

const Crimes = () => {
const [crimes,setCrimes] = useState([])

useEffect(()=>{
    const fetchAllCrimes = async()=>{
        try{
            const coords = {
                lat : 40.1020,
                lng : -88.2272
            }
            const res = await axios.post("http://localhost:3306/Crimes", coords)
            setCrimes(res.data);
            console.log(res)
            console.log(res.data[0].longitude)
        }catch(err){
            console.log(err)
        }
    }
    fetchAllCrimes();
    //var loggedInAs = document.getElementById("loggedInAs")
    //loggedInAs.innerHTML = "Logged in as <span class=\"curUser\">"+Cookies.get('username')+"</span>"
}, []);

const {isLoaded} = useLoadScript({googleMapsApiKey: "AIzaSyC0lAXu1gaIJAw-Q5Gibi3JTaRzFs-wc7I"});
if(!isLoaded) {return <div>Loading....</div>}

return (
    <div>
        <h1>Reported Crimes</h1>
        <span id="loggedInAs">Logged in as ...</span>
        <div>
            <button className="report"><Link to="/report">Report a Crime</Link></button>
            <button className="advQ1"><Link to="/advQ1">Stored Procedure</Link></button>
            <button className="toUsers"><Link to="/Users">View Users</Link></button>
        </div>
        <h5 id="long">Longitude: ?</h5> 
        <h5 id="lat">Latitude: ?</h5> 
        <div className="map_and_crimes">
            <div className="crime_table">
                <table className="crime_table_header">
                <thead>
                    <tr>
                        <th>Crime ID</th>
                        <th>Location ID</th>
                        <th>Crime Type</th>
                        <th>Time</th>
                        <th>User Reported</th>
                        <th>Victim ID</th>
                        <th>Longitude</th>
                        <th>Latitude</th>
                    </tr>
                </thead>
                </table>
                <div className="crime_table_body_wrapper">
                <table>
                    <tbody>
                    {crimes.map(crime => (
                        <tr key={crime.crimeID}>
                        <td>{crime.crimeID}</td>
                        <td>{crime.locationID}</td>
                        <td>{crime.crime_type}</td>
                        <td>{crime.time}</td>
                        <td>{crime.user_report}</td>
                        <td>{crime.victimID}</td>
                        <td>{crime.longitude}</td>
                        <td>{crime.latitude}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                </div>
            </div>
            <div className="map_container">
                <Map crimes={crimes}/>
            </div>
        </div>
        
    </div>
  )
}

export default Crimes

function Map({crimes}) {
const [selectedCrime, setSelectedCrime] = useState(null);
const [postCrime, setPostCrime] = useState(null);

if (crimes.length === 0) {
    return <div>Loading...</div>;
}

const sexOptions = [
    { value: 'M', label: 'Male' },
    { value: 'F', label: 'Female' },
    { value: 'X', label: 'Other' },
  ];
const handleSubmit =  async e=> {
    e.preventDefault()
    try {
      const res = await axios.post("http://localhost:3306/AddCrime", postCrime)
      console.log(res)
      window.location.reload()
    }catch(err){
      console.log(err)
    }
}
const handleChange = (e) => {
    const name = e.target.name;
    let value = e.target.value;
    if (name === "time") {
        value = value.replace(":", "");
    }
    console.log("handling change! " + value)
    setPostCrime((prev) => {
        return { ...prev, [name]: value };
    });
};
const handleSelect = (e, actionMeta) => {
    const name = actionMeta.name;
    let value = e.value;
    console.log("handling select! " + value)
    setPostCrime((prev) => {
      return { ...prev, [name]: value };
    });
};

//console.log(crimes[0].latitude + )
 //console.log(crimes[0].longitude + " " + crimes[0].latitude)
 return <GoogleMap zoom={15} center={{lat: 40.1020, lng:-88.2272}} mapContainerClassName='map' onClick={ev => {
    lat = ev.latLng.lat()
    lng = ev.latLng.lng()
    document.getElementById('lat').innerHTML = "Latitude: <span class=\"blue\">" + lat + "</span>";
    document.getElementById('long').innerHTML = "Longitude: <span class=\"blue\">" + lng + "</span>";
    setPostCrime({
        crimeType: "",
        crimeLat: lat,
        crimeLng: lng,
        crimeDescription: "",
        victimSex: "",
        victimRace: "",
        victimAge: "",
        time: "",
        username: Cookies.get('username')
    })
    }}>
    {crimes.map((crime, index) => (
        <MarkerF
          key={index}
          position={{ lat: crime.latitude, lng: crime.longitude }}
          onClick={() => {
            setSelectedCrime(crime);
          }}
          options={{
            icon: {
              url: 'https://maps.google.com/mapfiles/ms/icons/red-dot.png', // Replace with the URL of your icon
              scaledSize: new window.google.maps.Size(50, 50), // Set the size of the icon
            },
            label: crime.crimeID
          }}
        />
      ))}
    {postCrime && (
        <InfoWindow
          position={{ lat: postCrime.crimeLat, lng: postCrime.crimeLng }}
          onCloseClick={() => {
            setPostCrime(null);
          }}
        >
            <form onSubmit={handleSubmit}>
                <h5>Crime Type: </h5> 
                <Select
                    options={crimeTypeOptions}
                    name="crimeType"
                    onChange={handleSelect}
                />
                <h5>Crime Description: </h5> <textarea name="crimeDescription" onChange={handleChange}></textarea>
                <h5>Victim Sex: </h5>
                <Select
                    options={sexOptions}
                    name="victimSex"
                    onChange={handleSelect}
                />
                <h5>Time: </h5> <input type="time" name="time" onChange={handleChange}/>
                <h5>Victim Race: </h5>
                <Select
                    options={raceOptions}
                    name="victimRace"
                    onChange={handleSelect}
                />
                <h5>Victim Age: </h5> <input type="number"  name="victimAge"onChange={handleChange}/>
                <button type="submit">Submit Form</button>
            </form>
        </InfoWindow>
      )}
    {selectedCrime && (
        <InfoWindow
          position={{ lat: selectedCrime.latitude, lng: selectedCrime.longitude }}
          onCloseClick={() => {
            setSelectedCrime(null);
          }}
        >
          <div>
            <h4>{selectedCrime.crime_type}</h4>
            <p>
              Crime ID: {selectedCrime.crimeID} <br />
              Location ID: {selectedCrime.locationID} <br />
              Time: {selectedCrime.time} <br />
              User Reported: {selectedCrime.user_report} <br />
              Victim ID: {selectedCrime.victimID} <br />
              Description: {selectedCrime.description}
            </p>
            <button className="delete" onClick={()=>handleDelete(selectedCrime.crimeID)}>Delete</button>
            <button className="update"><Link to={`/update/${selectedCrime.crimeID}`}>Update</Link></button>
          </div>
        </InfoWindow>
      )}
 </GoogleMap>
 }
