import React from 'react'
import {useEffect} from 'react'
import {useState} from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import MapSection from '../components/Map'
import Cookies from 'js-cookie'

const Crimes = () => {
const [crimes,setCrimes] = useState([])

const location = {
    address: '1600 Amphitheatre Parkway, Mountain View, california.',
    lat: 37.42216,
    lng: -122.08427,
}

useEffect(()=>{
    const fetchAllCrimes = async()=>{
        try{
            const res = await axios.get("http://localhost:3306/Crimes")
            setCrimes(res.data);
            console.log(res)
            console.log(res.data[0])
        }catch(err){
            console.log(err)
        }
    }
    fetchAllCrimes();
    var loggedInAs = document.getElementById("loggedInAs")
    loggedInAs.innerHTML = "Logged in as <span class=\"curUser\">"+Cookies.get('username')+"</span>"
}, []);

return (
    <div>
        <h1>Reported Crimes</h1>
        <span id="loggedInAs">Logged in as ...</span>
        {/* <MapSection location={location} zoomLevel={17} /> {/* include it here */}
        <div>
            <button className="report"><Link to="/report">Report a Crime</Link></button>
            <button className="advQ1"><Link to="/advQ1">Adv Query 1</Link></button>
            <button className="advQ2"><Link to="/advQ2">Adv Query 2</Link></button>
            <button className="toUsers"><Link to="/Users">View Users</Link></button>
        </div>
        <table>
            <thead>
                <tr>
                    <th>Crime ID</th>
                    <th>Location ID</th>
                    <th>Crime Type</th>
                    <th>Time</th>
                    <th>User Reported</th>
                    <th>Victim ID</th>
                </tr>
            </thead>
            {crimes.map(crime => (
                <tbody>
                    <tr>
                        <td>{crime.crimeID}</td>
                        <td>{crime.locationID}</td>
                        <td>{crime.crime_type}</td>
                        <td>{crime.time}</td>
                        <td>{crime.user_report}</td>
                        <td>{crime.victimID}</td>
                    </tr>
                </tbody>
            ))}
        </table>
    </div>
  )
}

export default Crimes
