import React from 'react';
import {useEffect} from 'react'
import {useState} from 'react';
import axios from 'axios'
import { useNavigate } from 'react-router-dom';
import { GoogleMap, useLoadScript, Markers} from '@react-google-maps/api';
import Cookies from 'js-cookie'

var lat = 0
var lng = 0

const Report = () => {

  useEffect(() => {
    const dropdown = document.querySelector(".dropdown");
    if (!dropdown) return;
  
    // Code to hide the dropdown list when the user clicks outside of it
    document.addEventListener("click", function(e) {
      if (!dropdown.contains(e.target)) {
        dropdown.querySelector(".dropdown-list").style.display = "none";
      }
    });
  
    // Code to filter the dropdown list based on user input
    const input = dropdown.querySelector(".dropdown-search");
    const list = dropdown.querySelector(".dropdown-list");
    const options = Array.from(list.querySelectorAll("li"));
  
    input.addEventListener("input", function() {
      const value = this.value.trim().toLowerCase();
      options.forEach(function(option) {
        if (option.textContent.toLowerCase().indexOf(value) !== -1) {
          option.style.display = "block";
        } else {
          option.style.display = "none";
        }
      });
    });
  
    // Clear the event listener when the component unmounts
    return () => {
      document.removeEventListener("click", function(e) {
        if (!dropdown.contains(e.target)) {
          dropdown.querySelector(".dropdown-list").style.display = "none";
        }
      });
    };
  }, []);
  
  

  // return () => clearInterval(interval);
  // }, [])

  const [details, setDetails] = useState({
    crimeType: "",
    crimeDescription: "",
    victimSex: "",
    victimRace: "",
    victimAge: "",
    time: "",
  });

  const handleChange = (e) => {
    const name = e.target.name;
    let value = e.target.value;
    if (name === "time") {
      value = value.replace(":", "");
    }
    console.log("handling change! " + value)
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };

  const handleSubmit =  async e=> {
    e.preventDefault()
    try {
      console.log(details)
      const newDetails = {
        crimeType: details.crimeType,
        crimeLat: lat,
        crimeLng: lng,
        crimeDescription: details.crimeDescription,
        victimSex: details.victimSex,
        victimRace: details.victimRace,
        victimAge: details.victimAge,
        time: details.time,
        username: Cookies.get('username')
      }
      const res = await axios.post("http://localhost:3306/AddCrime", newDetails)
      console.log(res)
    }catch(err){
      console.log(err)
    }
    
  }
  const {isLoaded} = useLoadScript({googleMapsApiKey: "AIzaSyC0lAXu1gaIJAw-Q5Gibi3JTaRzFs-wc7I"});
  if(!isLoaded) {return <div>Loading....</div>}

  return (
  <div>
    <h1>Report a crime!</h1>
    <form onSubmit={handleSubmit}>
      <h5>Crime Type: </h5> <input type="number" name="crimeType" onChange={handleChange} />
      <h5 id="lat">Latitude: ?</h5> 
      <h5 id="long">Longitude: ?</h5> 
      <h5>Crime Description: </h5> <textarea  name="crimeDescription" onChange={handleChange}></textarea>
      <h5>Victim Sex: </h5>
      <select name="victimSex" onChange={handleChange}>
         <option value="M">Male</option>
         <option value="F">Female</option>
         <option value="X">Other</option>
       </select>
      <h5>Time: </h5> <input type="time" name="time" onChange={handleChange}/>
      <h5>Victim Race: </h5>
      <select name="victimRace" onChange={handleChange}>
        <option value="A">Other Asian</option>
        <option value="B">Black</option>
        <option value="C">Chinese</option>
        <option value="D">Cambodian</option>
        <option value="F">Filipino</option>
        <option value="G">Guamanian</option>
        <option value="H">Hispanic/Latin/Mexican</option>
        <option value="I">American Indian/Alaskan Native</option>
        <option value="J">Japanese</option>
        <option value="K">Korean</option>
        <option value="L">Laotian</option>
        <option value="O">Other</option>
        <option value="P">Pacific Islander</option>
        <option value="S">Samoan</option>
        <option value="U">Hawaiian</option>
        <option value="V">Vietnamese</option>
        <option value="W">White</option>
        <option value="X">Unknown</option>
        <option value="Z">Asian Indian</option>
      </select>
      <h5>Victim Age: </h5> <input type="number"  name="victimAge"onChange={handleChange}/>
      <button type="submit">Submit Form</button>
    </form>
    <Map/>
  </div>
  )
}

export default Report

function Map() {
 return <GoogleMap onClick={ev => {
    lat = ev.latLng.lat()
    lng = ev.latLng.lng()
    document.getElementById('lat').innerHTML = "Latitude: <span class=\"blue\">" + lat + "</span>";
    document.getElementById('long').innerHTML = "Longitude: <span class=\"blue\">" + lng + "</span>";
 }} zoom={15} center={{lat: 40.1020, lng:-88.2272}} mapContainerClassName='map'></GoogleMap>
}
